import React from 'react'
import AdminLayout from '../../../Hoc/AdminLayout'

import FormField from '../../ui/formFields'
import { validate } from '../../ui/misc'

class AddEditMatch extends React.Component {

  state = {
      matchId:'',
      formType:'',
      formError:false,
      formSuccess:'',
      teams:[],
      formdata:{
        date:{
            element:'input',
            value:'',
            config:{
                label: 'Event date',
                name:'date_input',
                type: 'date',
            },
            validation:{
                required: true
            },
            valid: false,
            validationMessage:'',
            showlabel: true
        },

        local:{
            element:'select',
            value:'',
            config:{
                label: 'Select a local team',
                name:'select_local',
                type: 'select',
                options: []
            },
            validation:{
                required: true
            },
            valid: false,
            validationMessage:'',
            showlabel: false
        },
      }

  }
  render () {
      return (
            <AdminLayout>
              <div className="editmatch_dialog_wrapper">
                <h2>
                    {this.state.formType}
                </h2>
                <div>
                  <form onSubmit={(event)=> this.submitForm(event)}>

                    <FormField
                        id={'date'}
                        formdata={this.state.formdata.date}
                        change={(element)=> this.updateForm(element)}
                      />

                    <FormField
                        id={'local'}
                        formdata={this.state.formdata.local}
                        change={(element)=> this.updateForm(element)}
                      />
                  </form>
                </div>
              </div>
            </AdminLayout>
      )
  }
}

export default AddEditMatch;
